<section class="flexbox">
  <div class="parent1">
    <div class="parent1child2">{{todo?.date}}</div>
    <div class="parent1child1">
      <input
        class="form-checkinput"
        type="checkbox"
        value=""
        id="flexCheckChecked"
        (click)="onCheckBoxClick(todo)"
      />
      <label class="form-check-label px-2" for="flexCheckChecked"> Task Done </label>
    </div>
  </div>

  <div class="parent2">
    <div [ngClass]="{ strike: !todo?.active }" class="parent2child1">{{ todo?.title }}</div>
    <div class="parent2child2">
      <div class="description-container">
        <p class="description-text">{{todo?.description}}</p>
      </div>
    </div>
  </div>

  <div class="parent3">
